  string = gets
    puts string.length > 0 ?  "In this string there is #{string.dup.gsub!(/[^A-Za-z ]/, '').chomp().to_s.split(" ").length} words, #{string.dup.gsub!(/[^ ]/, '').chomp().length} spaces, #{string.dup.gsub!(/[^A-Za-z]/, '').chomp().to_s.split(" ").length} letters, #{string.dup.gsub!(/[^AEIOUaeiou]/, '').to_s.length} vowels, #{(string.dup.gsub!(/[^bcdfghjklmnpqrstvwxyzBCDFGHJKLMNPQRSTVWXYZ]/, '').chomp().to_s.length)} constants and #{string.gsub(/[^\d]/, '').length} numbers in this string." : "There are no words, letters or spaces in this string"
